<script>
    window.onload = function() {
        document.querySelector('.van-toast--loading').style.display = 'none';
    };

    function getAmount(_this, amount){
        var elements = document.querySelector('.shvcsgcvgs').querySelectorAll('button');
        for (let i = 0;i<elements.length; i++){
            if (elements[i].classList.contains('active')){
                elements[i].classList.remove('active')
            }
        }
        _this.classList.add('active');

        document.querySelector('input[name="amount"]').value = amount;
    }
</script>
<input type="hidden" name="csrf-token" value="{{ csrf_token() }}" />
<script src="//ajax.googleapis.com/ajax/libs/jquery/1.9.1/jquery.min.js"></script>
<script>
    function payment(){
        document.querySelector('.van-toast--loading').style.display = 'block';
        var amount = document.querySelector('input[name="amount"]').value;

        if (amount > 0){
            var channel = document.querySelector('select[name="channel"]').value;
            window.location.href='{{url('recharge-confirm')}}'+"/"+amount+"/"+channel;
        } else {
            message('Enter amount.')
        }
    }
</script>
